mapper.select:
  		select * 
		from member
		where id=#{id}
		and password=#{password};

  		select count(*)
		from member
		where id= #{id}
		and password=#{password};

  		select count(*)
		from member
		where id= #{id};

  		select id
		from member
		where name= #{name} and email= #{email};

  		select *
		from member
		where id=#{id} and name= #{name} and email= #{email};

mapper.update:
update member
set password=#{password} 
where id=#{id};
